  `weight` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,
  `cpu` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,
  `ram` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,
  `rom` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,
  `battery` varchar(16) COLLATE utf8_unicode_ci DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

